import axios from 'axios'
import ReactMarkdown from 'react-markdown'

export default function Index ({ markdown }) {
  return (
    <div>
      <p>my dope page with md files</p>
    </div>
  )
}

Index.getInitialProps = async function () {
  const importAll = (r) => r.keys().map(r)
  const markdownFiles = importAll(require.context('./../posts', false, /\.md$/))
  const fetchFileContent = filePath => {
    console.log(markdownFiles)
    return axios.get(filePath.default)
  }
  const response = await Promise.all(markdownFiles.map(fetchFileContent))
  return { markdown: response.data }
}
