import axios from 'axios'
import ReactMarkdown from 'react-markdown'

export default function Index ({ markdown }) {
  return (
    <div>
      <p>my dope page with md files</p>
    </div>
  )
}

Index.getInitialProps = async function () {
//   const importAll = (r) => r.keys().map(r)
  const markdownFiles = require.context('./../posts', true, /\.md$/).keys()
  const files = await Promise.all(markdownFiles.map(file => axios.get(file)))
  console.log(files)
  return { markdown: markdownFiles }
}
