import axios from 'axios'
import ReactMarkdown from 'react-markdown'

export default function Index ({ markdown }) {
  return (
    <div>
      {markdown.map(element => (<ReactMarkdown source={element} />))}
    </div>
  )
}

Index.getInitialProps = function () {
  const importAll = (r) => r.keys().map(r).map(file => file.default)
  const markdownFiles = importAll(require.context('./../posts', true, /\.md$/)).reverse()
  console.log(markdownFiles)
  return { markdown: markdownFiles }
}
